import React from "react";

const btnActions = {
  multiply: "multiply",
  divide: "divide",
  add: "add",
  subtract: "subtract",
  clear: "clear",
  percent: "percent",
};

const initialPayload = {
  result: 0,
  currentInput: 0,
  oldInput: 0,
  action: null,
};
export const useCalculator = () => {
  const [calc, setCalc] = React.useState(initialPayload);
  const arithmeticOperations = ["*", "/", "+", "-"];

  const { currentInput, oldInput, action } = calc;

  const addNumbers = () => {
    setCalc({
      ...calc,
      action: btnActions.add,
      oldInput: parseInt(calc.currentInput) + parseInt(calc.oldInput),
      result: parseInt(calc.currentInput) + parseInt(calc.oldInput),
    });
  };

  const subtractNumbers = () => {
    if (oldInput !== 0 && currentInput !== 0) {
      setCalc({
        ...calc,
        currentInput: oldInput - currentInput,
        action: btnActions.subtract,
        result: oldInput - currentInput,
      });
    } else {
      setCalc({
        ...calc,
        action: "subtract",
      });
    }
  };

  const multiplyNumbers = () => {
    setCalc({
      ...calc,
      action: btnActions.multiply,
      oldInput: parseInt(calc.currentInput) * parseInt(calc.oldInput),
      result: parseInt(calc.currentInput) * parseInt(calc.oldInput),
    });
  };

  const divideNumbers = () => {
    setCalc({
      ...calc,
      action: btnActions.divide,
      oldInput: parseInt(calc.currentInput) / parseInt(calc.oldInput),
      result: parseInt(calc.currentInput) / parseInt(calc.oldInput),
    });
  };

  const selectNumbers = (label) => {
    if (calc.action?.length) {
      setCalc({
        ...calc,
        oldInput: currentInput,
        currentInput: Number(`${calc.currentInput}${label}`),
      });
    } else {
      setCalc({
        ...calc,
        currentInput: Number(`${calc.currentInput}${label}`),
      });
    }
  };

  const clearNumbers = () => {
    setCalc(initialPayload);
  };

  const getFinalResult = () => {
    switch (action) {
      case btnActions.add:
        setCalc({
          ...calc,
          result: Number(calc.oldInput) + Number(calc.currentInput),
        });
        break;
      case btnActions.divide:
        setCalc({
          ...calc,
          result: Number(calc.oldInput) / Number(calc.currentInput),
        });
      case btnActions.multiply:
        setCalc({
          ...calc,
          result: Number(calc.oldInput) * Number(calc.currentInput),
        });
      case btnActions.percent:
        setCalc({
          ...calc,
          result: Number(calc.oldInput) % Number(calc.currentInput),
        });
      default:
        break;
    }
  };

  return {
    addNumbers,
    subtractNumbers,
    multiplyNumbers,
    divideNumbers,
    selectNumbers,
    clearNumbers,
    getFinalResult,
    currentInput,
    result,
  };
};
