import "./App.css";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { Button } from "./components/button/Button";
import { useCalculator } from "./hook/useCalculator";

const calcButtons = [
  [
    { label: "c" },
    { label: "+-" },
    { label: "%" },
    { label: "/", variant: "primary" },
  ],
  [
    {
      label: "7",
    },
    {
      label: "8",
    },
    {
      label: "9",
    },
    {
      label: "*",
      variant: "primary",
    },
  ],
  [
    {
      label: "4",
    },
    {
      label: "5",
    },
    {
      label: "6",
    },
    {
      label: "-",
      variant: "primary",
    },
  ],
  [
    {
      label: "1",
    },
    {
      label: "2",
    },
    {
      label: "3",
    },
    {
      label: "+",
      variant: "primary",
    },
  ],
  [
    {
      label: "0",
    },
    {
      label: ".",
    },
    {
      label: "=",
      variant: "primary",
    },
  ],
];

function App() {
  const {
    addNumbers,
    subtractNumbers,
    multiplyNumbers,
    divideNumbers,
    selectNumbers,
    clearNumbers,
    finalResult,
    currentInput,
    result,
  } = useCalculator();

  const handleBtnClick = (label) => {
    switch (label) {
      case "-":
        subtractNumbers(label);
        break;
      case "+":
        addNumbers(label);
        break;
      case "*":
        multiplyNumbers(label);
        break;
      case "/":
        divideNumbers(label);
        break;
      case "=":
        finalResult(label);
        break;
      case "c":
        clearNumbers(initialPayload);
        break;
      case ".":
        // setCalc({
        //   ...calc,
        //   currentInput: calc.currentInput.includes(".")
        //     ? calc.currentInput
        //     : `${calc.currentInput}.`,
        // });
        break;
      default:
        selectNumbers(label);
    }
  };

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value={result || currentInput || 0} />
        <div className="grid grid-cols-4 gap-4 mt-10">
          {calcButtons.flat().map(({ label, action, variant }) => {
            return (
              <Button
                label={label}
                key={label}
                variant={variant || "secondary"}
                onClick={() => handleBtnClick(label, action)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
