import "./App.css";
import { useState } from "react";
import { Title } from "./components/title/Title";
import { OutputScreen } from "./components/outputScreen/OutputScreen";

function App() {
  const [display, setDisplay] = useState("");
  const [expression, setExpression] = useState([]);



  const handleClick = (value) => {
    setDisplay(value);
    setExpression([...expression, value]);
  };

  const handleResult = () => {
    const result = expression
      .join("")
      .split(/(\D)/g)
      .map(value => (value.match(/\d/g) ? parseInt(value, 0) : value))
      .reduce((acc, value, index, array) => {
        switch (value) {
          case "+":
            return (acc = acc + array[index + 1]);
          case "-":
            return (acc = acc - array[index + 1]);
          case "x":
            return (acc = acc * array[index + 1]);
          case "รท":
            return (acc = acc / array[index + 1]);
          default:
            return acc;
        }
      });
    setDisplay(result);
    setExpression("");
  };

  return (
    <div className="App">
      <div className="h-screen flex justify-center items-center flex-col bg-slate-100">
        <Title />
        <div className="bg-primary  h-auto w-1/5 border rounded-xl px-8 py-20">
          <OutputScreen  display={display} expression={expression} setDisplay={setDisplay}/>

        </div>
      </div>
    </div>
  );
}

export default App;
