import React from "react";

export const OutputScreen = ({expression, display, setDisplay}) => {
  return (
    <div>
      <h1 className="text-white font-bold text-3xl mb-4">{expression}</h1>
      <div className="mb-4">
        <input
          type="text"
          className="text-white bg-white py-2 px-2"
          value={display}
          onChange={(e) => setDisplay(e.target.value)}
        />
      </div>
      <hr className="border border-x border-stone-50 mb-4" />
    </div>
  );
};
