import "./App.css";
import { useState } from "react";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { Button } from "./components/button/Button";
import { useCalculator } from "./hook/useCalculator";

const btnActions = {
  multiply: "multiply",
  divide: "divide",
  add: "add",
  subtract: "subtract",
  clear: "clear",
  percent: "percent",
};

const calcButtons = [
  [
    {
      label: "c",
      action: btnActions.clear,
    },
    {
      label: "+-",
      action: btnActions.clear,
    },
    {
      label: "%",
      action: btnActions.percent,
    },
    {
      label: "/",
      action: btnActions.divide,
      variant: "primary",
    },
  ],
  [
    {
      label: "7",
      action: null,
    },
    {
      label: "8",
      action: null,
    },
    {
      label: "9",
      action: null,
    },
    {
      label: "*",
      action: btnActions.multiply,
      variant: "primary",
    },
  ],
  [
    {
      label: "4",
      action: null,
    },
    {
      label: "5",
      action: null,
    },
    {
      label: "6",
      action: null,
    },
    {
      label: "-",
      action: btnActions.subtract,
      variant: "primary",
    },
  ],
  [
    {
      label: "1",
      action: null,
    },
    {
      label: "2",
      action: null,
    },
    {
      label: "3",
      action: null,
    },
    {
      label: "+",
      action: btnActions.add,
      variant: "primary",
    },
  ],
  [
    {
      label: "0",
      action: null,
    },
    {
      label: ".",
      action: null,
    },
    {
      label: "=",
      action: btnActions.equate,
      variant: "primary",
    },
  ],
];

const arithmeticOperations = ["*", "/", "+", "-"];
const initialPayload = {
  result: null,
  currentInput: "0",
  oldInput: "0",
  action: null,
};

function App() {
  // const [value, setvalue] = useState("")
  // const [calc, setCalc] = useState(initialPayload);
  const {
    addNumbers,
    subtractNumbers,
    multiplyNumbers,
    divideNumbers,
    selectNumbers,
    clearNumbers,
    finalResult,
    calc,
  } = useCalculator();

  const { currentInput, result } = calc;
  console.log("ðŸš€ ~ file: App.js ~ line 123 ~ App ~ calc", calc);

  const handleBtnClick = (label) => {
    switch (label) {
      case "-":
        subtractNumbers(label);
        break;
      case "+":
        addNumbers(label)
        break;
      case "*":
        multiplyNumbers(label);
        break;
      case "/":
        divideNumbers(label);
        break;
      case "=":
        finalResult(label);
        break;
      case "c":
        clearNumbers(initialPayload);
        break;
      case ".":
        // setCalc({
        //   ...calc,
        //   currentInput: calc.currentInput.includes(".")
        //     ? calc.currentInput
        //     : `${calc.currentInput}.`,
        // });
        break;
      default:
        selectNumbers(label);
    }
  };

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value={result || currentInput || 0} />
        <div className="grid grid-cols-4 gap-4 mt-10">
          {calcButtons.flat().map(({ label, action, variant }) => {
            return (
              <Button
                label={label}
                key={label}
                variant={variant || "secondary"}
                onClick={() => handleBtnClick(label, action)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
