import React from "react";
import { useMemo } from "react";

const availableBtnVariants = {
  primary: "primary",
  secondary: "secondary",
  tetiary: "tetiary"
}

export const Button = ({ label, onClick, variant }) => {

  const getBtnVariant = useMemo(() => {
    switch (variant) {
      case availableBtnVariants.primary:
        return "bg-orange-500"
      case availableBtnVariants.secondary:
        return "bg-gray-400"
      default:
        return "bg-black";
    }
  }, [variant])

  return (
    <button 
      className={`text-2xl py-1 px-4 border-0 rounded-md mb-2 text-white ${getBtnVariant}`}
      onClick={onClick}
      variant="primary"
    >
      {label}
    </button>
  );
};
