import "./App.css";
import { Keypad } from "./components/keypad/Keypad";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { useCalculator } from "./hook/useCalculator";

function App() {
  const { handleActionToPerform, screenValue, isScreenClear } = useCalculator();

  return (
    <div id="calculator-view" className="flex flex-col justify-center items-center">
      <div id="viewport" className="flex column jc-sp-between items-center">
        <OutputScreen value={screenValue} />
        <Keypad
          actionToPerform={handleActionToPerform}
          allClear={isScreenClear}
        />
      </div>
    </div>
  );
}

export default App;
