import React from "react";

const initialPayload = {
  result: 0,
  currentInput: 0,
  oldInput: 0,
  action: null,
};
export const useCalculator = () => {
  const [calc, setCalc] = React.useState(initialPayload);
  const arithmeticOperations = ["*", "/", "+", "-"];

  const { currentInput, oldInput } = calc;

  const subtractNumbers = (label) => {
    if (oldInput !== 0 && currentInput !== 0) {
      setCalc({
        ...calc,
        currentInput: oldInput - currentInput,
        result: oldInput - currentInput,
      });
    } else {
      setCalc({
        ...calc,
        currentInput: label,
      });
    }
  };

  const selectNumbers = (label) => {
    if (calc.action?.length) {
      setCalc({
        ...calc,
        currentInput: label,
        result: null,
      });
    } else {
      setCalc({
        ...calc,
        currentInput: `${calc.currentInput}${label}`
      });
    }
  };
  return {
    subtractNumbers,
    selectNumbers,
    calc,
  };

};
