import "./App.css";
import { Keypad } from "./components/keypad/Keypad";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { useCalculator } from "./hook/useCalculator";

function App() {
  const {
    handleActionToPerform,
    screenValue,
    isScreenClear,
  } = useCalculator();

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value={screenValue} />
        <Keypad
          actionToPerform={handleActionToPerform}
          allClear={isScreenClear}
        />
      </div>
    </div>
  );
}

export default App;
