import "./App.css";
import { useState } from "react";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { Button } from "./components/button/Button";

const btnActions = {
  multiply: "multiply",
  divide: "divide",
  add: "add",
  subtract: "subtract",
  clear: "clear",
  percent: "percent"

};

const calcButtons = [
  [
    {
      label: "c",
      action: btnActions.clear,
    },
    {
      label: "+-",
      action: btnActions.clear,
    },
    {
      label: "%",
      action: btnActions.percent,
    },
    {
      label: "/",
      action: btnActions.divide,
      variant: "primary",
    },
  ],
  [
    {
      label: "7",
      action: null,
    },
    {
      label: "8",
      action: null,
    },
    {
      label: "9",
      action: null,
    },
    {
      label: "X",
      action: btnActions.multiply,
      variant: "primary",
    },
  ],
  [
    {
      label: "4",
      action: null,
    },
    {
      label: "5",
      action: null,
    },
    {
      label: "6",
      action: null,
    },
    {
      label: "-",
      action: btnActions.subtract,
      variant: "primary",
    },
  ],
  [
    {
      label: "1",
      action: null,
    },
    {
      label: "2",
      action: null,
    },
    {
      label: "3",
      action: null,
    },
    {
      label: "+",
      action: btnActions.add,
      variant: "primary",
    },
  ],
  [
    {
      label: "0",
      action: null,
    },
    {
      label: ".",
      action: null,
    },
    {
      label: "=",
      action: btnActions.equate,
      variant: "primary",
    },
  ],
];

const arithmeticOperations = [ "X", "/", "+", "-"]
const initialPayload = {
  result: null,
  currentInput: "0",
  oldInput: "0",
  action: null
}

function App() {
  const [value, setvalue] = useState("")
  const [calc, setCalc] = useState(initialPayload)

  const handleBtnClick = (label, action) => {
    {console.log(action, "llll")}
    switch (label) {
      case arithmeticOperations.includes(label):
        setCalc({ 
          ...calc, 
          action,
          oldInput: calc.currentInput,
          ffff: "oooo"
        })
        break;
      case 'c': 
        setCalc(initialPayload);
        break;
      case '.': 
        setCalc({
          ...calc,
          currentInput: calc.currentInput.includes('.') ? calc.currentInput : `${calc.currentInput}.`
        })
        break;
      default: {
        if(calc.action) {
          setCalc({
            ...calc,
            currentInput: arithmeticOperations.includes(label) ? "0" :  calc.currentInput
          })
        }
        setCalc({
          ...calc,
          currentInput: arithmeticOperations.includes(label) ? calc.currentInput : `${calc.currentInput}${label}`
        })
        break;
      }
    }
  };

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        {console.log(calc)}
        <OutputScreen value={calc.result ? calc.result : calc.currentInput} />
        <div className="grid grid-cols-4 gap-4 mt-10">
          {calcButtons.flat().map(({ label, action, variant }) => {
            return (
              <Button
                label={label}
                key={label.toString()}
                variant={variant ?? 'secondary'}
                onClick={() => handleBtnClick(label, action)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
