import "./App.css";
import { useState } from "react";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { Button } from "./components/button/Button";

const btnActions = {
  multiply: "multiply",
  clear: "clear",
  percent: "percent",
  divide: "divide",
  add: "add",
  subtract: "subtract"
};

function App() {
  const calcButtons = [
    [
      {
        label: "c",
        action: btnActions.clear,
        variant: "secondary",
      },
      {
        label: "+-",
        action: btnActions.clear,
        variant: "secondary",
      },
      {
        label: "%",
        action: btnActions.percent,
        variant: "secondary",
      },
      {
        label: "/",
        action: btnActions.divide,
        variant: "primary",
      },
    ],
    [
      {
        label: "7",
        action: null,
        variant: "secondary",
      },
      {
        label: "8",
        action: null,
        variant: "secondary",
      },
      {
        label: "9",
        action: null,
        variant: "secondary",
      },
      {
        label: "X",
        action: btnActions.multiply,
        variant: "primary",
      },
    ],
    [
      {
        label: "4",
        action: null,
        variant: "secondary",
      },
      {
        label: "5",
        action: null,
        variant: "secondary",
      },
      {
        label: "6",
        action: null,
        variant: "secondary",
      },
      {
        label: "-",
        action: btnActions.subtract,
        variant: "primary",
      },
    ],
    [
      {
        label: "1",
        action: null,
        variant: "secondary",
      },
      {
        label: "2",
        action: null,
        variant: "secondary",
      },
      {
        label: "3",
        action: null,
        variant: "secondary",
      },
      {
        label: "+",
        action: btnActions.add,
        variant: "primary",
      },
    ],
    [
      {
        label: "0",
        action: null,
        variant: "secondary",
      },
      {
        label: ".",
        action: null,
        variant: "secondary",
      },
      {
        label: "=",
        action: btnActions.equate,
        variant: "primary",
      },
    ],
  ];

  // const [display, setDisplay] = useState("");
  // const [expression, setExpression] = useState([]);

  // const handleClick = (value) => {
  //   setDisplay(value);
  //   setExpression([...expression, value]);
  // };

  const handleResult = () => {
    const result = expression
      .join("")
      .split(/(\D)/g)
      .map((calcButtons) => (calcButtons.match(/\d/g) ? parseInt(value, 0) : value))
      .reduce((acc, value, index, array) => {
        switch (value) {
          case "add":
            return (acc = acc + array[index + 1]);
          case "subtract":
            return (acc = acc - array[index + 1]);
          case "multiply":
            return (acc = acc * array[index + 1]);
          case "divide":
            return (acc = acc / array[index + 1]);
          default:
            return acc;
        }
      });
    setDisplay(result);
    setExpression("");
  };

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value="0" />
        <div className="grid grid-cols-4 gap-4 mt-10">
          {calcButtons.flat().map((btn, i) => {
            console.log(btn);
            return (
              <Button
                label={btn.label}
                key={i}
                variant={btn.variant}
                onClick={handleResult}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
