import "./App.css";
import { Keypad } from "./components/keypad/Keypad";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { useCalculator } from "./hook/useCalculator";

const numericKeys = [7, 8, 9, 4, 5, 6, 1, 2, 3];

const operatorKeys = [
  { label: "÷", value: "/" },
  { label: "×", value: "x" },
  { label: "-", value: "-" },
  { label: "+", value: "+" },
  { label: "=", value: "=" },
];

const functionKeys = [
  { label: allClear ? "AC" : "C", value: allClear ? "AC" : "C" },
  { label: "±", value: "+/-" },
  { label: "%", value: "%" },
];

const lastRowKeys = [
  {
    label: "0",
    value: "0",
    type: "numeric",
    buttonStyle: "numeric-key special",
  },
  { label: "·", value: ".", type: "fx", buttonStyle: "numeric-key" },
];

function App() {
  const {
    handleActionToPerform,
    screenValue,
    expectsOperand,
    isScreenClear,
  } = useCalculator();

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value={screenValue} />
        <Keypad
          actionToPerform={handleActionToPerform}
          allClear={isScreenClear}
        />
      </div>
    </div>
  );
}

export default App;
