import React from "react";

const initialPayload = {
  result: null,
  currentInput: null,
  oldInput: null,
  action: null,
};
export const useCalculator = (label) => {
  const [calc, setCalc] = React.useState(initialPayload);
  const arithmeticOperations = ["*", "/", "+", "-"];

  const subtractNumbers = () => {
    if (calc.oldInput && calc.newInput) {
      setCalc({
        ...calc,
        currentInput: parseInt(calc.oldInput) - parseInt(calc.currentInput),
        result: parseInt(calc.oldInput) - parseInt(calc.currentInput),
      });
    } else {
      setCalc({
        ...calc,
        currentInput: label,
      });
    }
  };

  const selectNumbers = () => {
    if (calc.action?.length) {
      setCalc({
        ...calc,
        currentInput: label,
        result: null,
      });
    } else {
      setCalc({
        ...calc,
        currentInput: arithmeticOperations.includes(label)
          ? calc.currentInput
          : `${calc.currentInput}${label}`,
      });
    }
  };
  return {
    subtractNumbers,
    selectNumbers,
    calc,
  };

};
