import "./App.css";
import { useState } from "react";
import { OutputScreen } from "./components/outputScreen/OutputScreen";
import { Button } from "./components/button/Button";

const btnActions = {
  multiply: "multiply",
  divide: "divide",
  add: "add",
  subtract: "subtract",
  clear: "clear",
  percent: "percent"

};

const calcButtons = [
  [
    {
      label: "c",
      action: btnActions.clear,
    },
    {
      label: "+-",
      action: btnActions.clear,
    },
    {
      label: "%",
      action: btnActions.percent,
    },
    {
      label: "/",
      action: btnActions.divide,
      variant: "primary",
    },
  ],
  [
    {
      label: "7",
      action: null,
    },
    {
      label: "8",
      action: null,
    },
    {
      label: "9",
      action: null,
    },
    {
      label: "X",
      action: btnActions.multiply,
      variant: "primary",
    },
  ],
  [
    {
      label: "4",
      action: null,
    },
    {
      label: "5",
      action: null,
    },
    {
      label: "6",
      action: null,
    },
    {
      label: "-",
      action: btnActions.subtract,
      variant: "primary",
    },
  ],
  [
    {
      label: "1",
      action: null,
    },
    {
      label: "2",
      action: null,
    },
    {
      label: "3",
      action: null,
    },
    {
      label: "+",
      action: btnActions.add,
      variant: "primary",
    },
  ],
  [
    {
      label: "0",
      action: null,
    },
    {
      label: ".",
      action: null,
    },
    {
      label: "=",
      action: btnActions.equate,
      variant: "primary",
    },
  ],
];

const arithmeticOperations = [ "X", "/", "+", "-"]

function App() {
  const [value, setvalue] = useState("")
  const [calc, setCalc] = useState({
    result: 0,
    currentValue: 0,
    newValue: 0,
    action: null
  })

  const handleBtnClick = (label) => {
    switch (action) {
      case btnActions.multiply,
            btnActions.add,
            btnActions.divide,
            btnActions.percent,
            btnActions.subtract:
        setCalc({
          ...calc,
          action
        })
        break;

      default:
        break;
    }
  };

  return (
    <div className="App h-screen flex justify-center items-center flex-col bg-slate-100">
      <div className="bg-primary h-auto w-1/4 border rounded-xl px-8 py-20">
        <OutputScreen value="0" />
        <div className="grid grid-cols-4 gap-4 mt-10">
          {calcButtons.flat().map((btn) => {
            return (
              <Button
                label={btn.label}
                key={btn.label.toString()}
                variant={btn.variant ?? 'secondary'}
                onClick={() => handleBtnClick(label)}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default App;
